import { makeStyles } from '@material-ui/core/styles'

const useStyles = makeStyles(theme => ({
  root: { marginBottom: theme.spacing(1), padding: theme.spacing(2) },
  link: { textDecoration: 'none', color: 'inherit' },
}))

export default useStyles
