//@flow
import * as React from 'react'
import useStyles from './styles'
import { Card, Typography, Breadcrumbs } from '@material-ui/core'

const SchoolCardMini = ({ school }: { school: Object }): React.Element<any> => {
  const c = useStyles()

  return (
    <Card className={c.root}>
      <a href={`${school.url}`} className={c.link}>
        <Typography variant="body2">{school.name}</Typography>
        <Breadcrumbs aria-label={`link to ${school.name_en} page`}>
          <Typography variant="caption">{school.schoolType}</Typography>
          <Typography variant="caption">{school.yearType}</Typography>
          <Typography variant="caption">{school.stateValue}</Typography>
        </Breadcrumbs>
      </a>
    </Card>
  )
}

export default SchoolCardMini
