// @flow
import * as React from 'react'
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  InputBase,
  MenuItem,
  Grow,
  Popper,
  ClickAwayListener,
  Paper,
  MenuList,
  List,
  ListItem,
  ListItemText,
  Divider,
} from '@material-ui/core/'
import { connectSearchBox, connectHits } from 'react-instantsearch-dom'
// import { connectSearchBox } from 'react-instantsearch-dom';

import { Search as SearchIcon, AccountCircle } from '@material-ui/icons'
import { Link } from 'react-router-dom'
import useStyles from './styles.js'

const Header = (): React.Element<any> => {
  const c = useStyles()
  const anchorRef = React.useRef(null)
  const [open, setOpen] = React.useState(false)
  const [hitsOpen, setHitsOpen] = React.useState(false)

  const onLogoutClick = () => {
    console.log('Logout button clicked!')
  }

  const handleToggle = () => {
    setOpen(prevOpen => !prevOpen)
  }

  const handleClose = event => {
    if (anchorRef.current && anchorRef.current.contains(event.target)) {
      return
    }
    setOpen(false)
  }

  function handleListKeyDown(event) {
    if (event.key === 'Tab') {
      event.preventDefault()
      setOpen(false)
    }
  }

  const SearchBox = ({ currentRefinement, refine }) => {
    return (
      <div className={c.search} component="form">
        <InputBase
          className={c.searchInputBase}
          placeholder="大学名で検索する..."
          classes={{
            root: c.inputRoot,
          }}
          type="search"
          value={currentRefinement}
          onFocus={() => {
            setHitsOpen(true)
          }}
          onBlur={() => {
            setHitsOpen(false)
          }}
          onChange={event => refine(event.currentTarget.value)}
          inputProps={{ 'aria-label': 'search U.S. colleges' }}
        />
        <IconButton type="submit" className={c.searchIcon} aria-label="search">
          <SearchIcon />
        </IconButton>
      </div>
    )
  }

  const CustomSearchBox = connectSearchBox(SearchBox)

  const Hits = ({ hits }) => {
    return (
      <List className={c.hitsList}>
        {hits.map(hit => (
          <ListItem className={c.hitItem}>{hit.name_en}</ListItem>
        ))}
      </List>
    )
  }

  const CustomHits = connectHits(Hits)

  return (
    <div className={c.grow} id="back-to-top-anchor">
      <AppBar position="static" className={c.headerRoot}>
        <Toolbar className={c.toolbarRoot}>
          <Typography className={c.title} variant="h6" noWrap>
            <a href="/" className={c.titleLink}>
              FORIS
            </a>
          </Typography>
          <div className={c.grow} />
          <div className={c.algoliaContainer}>
            <CustomSearchBox />
            <div className={c.hitsContainer}>
              <CustomHits />
            </div>
          </div>
          <div className={c.profileMenu}>
            <IconButton
              edge="end"
              aria-label="account of current user"
              color="black"
              ref={anchorRef}
              aria-controls={open ? 'menu-list-grow' : undefined}
              aria-haspopup="true"
              onClick={handleToggle}>
              <AccountCircle />
            </IconButton>
            <Popper
              open={open}
              anchorEl={anchorRef.current}
              role={undefined}
              transition
              disablePortal>
              {({ TransitionProps, placement }) => (
                <Grow
                  {...TransitionProps}
                  style={{
                    transformOrigin:
                      placement === 'bottom' ? 'center top' : 'center bottom',
                  }}>
                  <Paper>
                    <ClickAwayListener onClickAway={handleClose}>
                      <MenuList
                        autoFocusItem={open}
                        id="menu-list-grow"
                        onKeyDown={handleListKeyDown}>
                        <MenuItem
                          className={c.profileMenuItem}
                          onClick={() => handleClose()}>
                          <Typography variant="body2">
                            <Link to="/mypage" className={c.menuItemLink}>
                              プロフィール
                            </Link>
                          </Typography>
                        </MenuItem>
                        <MenuItem
                          className={c.profileMenuItem}
                          onClick={() => onLogoutClick()}>
                          <Typography variant="body2">ログアウト</Typography>
                        </MenuItem>
                      </MenuList>
                    </ClickAwayListener>
                  </Paper>
                </Grow>
              )}
            </Popper>
          </div>
        </Toolbar>
      </AppBar>
    </div>
  )
}

export default Header
