// @flow
import * as React from 'react'
import { useQuery } from 'react-query'
import { googleProvider, firebaseAuth } from '../util/firebase'
import { api } from '../util/api'
import { AuthContext } from '../AuthContext'
import axios from 'axios'

export const signUpWithEmail = async ({
  email,
  password,
}: {
  email: string,
  password: string,
}): Function => {
  try {
    const user = await firebaseAuth.createUserWithEmailAndPassword(
      email,
      password
    )
    const token = await user.getIdToken(true)
    const { data } = await api.put(
      'http://localhost:8003/users/',
      {
        headers: { Authorization: `Bearer ${token}` },
      },
      user
    )
    return data
  } catch (err) {
    console.log(err)
  }
}

export const signInWithEmail = async ({
  email,
  password,
}: {
  email: string,
  password: string,
}): Promise<any> => {
  try {
    const { user } = await firebaseAuth.signInWithEmailAndPassword(
      email,
      password
    )
    const token = await user.getIdToken(true)
    const { data } = await axios.get(`http://localhost:8003/users/`, {
      headers: { Authorization: `Bearer ${token}` },
    })
    return data.user
  } catch (err) {
    console.log(err)
  }
}

export const signInWithGoogle = async (): Promise<any> => {
  try {
    const { user } = await firebaseAuth.signInWithPopup(googleProvider)
    const token = await user.getIdToken(true)
    const { data } = await axios.get(`http://localhost:8003/users/`, {
      headers: { Authorization: `Bearer ${token}` },
    })
    return data
  } catch (err) {
    console.log(err)
  }
}

export const forgotPassword = async () => {}

export const useSignOut = async () => {
  const { setUser, setGlobalLoading } = React.useContext(AuthContext)
  setGlobalLoading(true)
  const result = await firebaseAuth
    .signOut()
    .then(() => {
      console.log('sign out success!')
      return 'success'
    })
    .catch(err => {
      console.log(err)
      return 'failure'
    })
  if (result) {
    setGlobalLoading(false)
    return true
  }
}

export const deleteAccount = () => {}

export const likeSchoolById = async (schoolId: string): Promise<any> => {
  try {
    const user = await firebaseAuth.currentUser
    const token = await user.getIdToken()
    const { data } = await axios.get(
      `http://localhost:3080/api/v1/users/like/?schoolId=${schoolId}`,
      {
        headers: { Authorization: `Bearer ${token}` },
      }
    )
    return data
  } catch (err) {
    console.log(
      'ðŸš€ ~ file: useSchools.js ~ likeSchoolById() try-catch ~ err',
      err
    )
  }
}

